def NewWait(request,delay:float) -> None:
    global bStopWait
    while request.resp_status is None:
        request._dispatch_result(delay)
        sleep(delay)
        if bStopWait:
            bStopWait=False
            break


def OnError(request,error) -> None:
    global bStopWait
    print ("Error:"+str(error))
    bStopWait      = True

def OnFailure(request,result) -> None:
    global bStopWait
    print ("Failure:"+str(result))
    bStopWait      = True

def OnReceive(oRequest,oResult) -> None:
    global bStopWait
    bStopWait      = True


